      ascending: true)
    ),
  with(
    local!datasubset:
    /*NOTE: Pass in zero as death cause to filter to only animals that are alive*/
    #"_a-0000db88-8ba1-8000-c510-010000010000_2248"(zooId: ri!zoo.id, deathCause: 0, pagingInfo: local!pagingInfo),
  
  #"SYSTEM_SYSRULES_dashboardLayout"(
    
