    #"_a-0000db88-8ba1-8000-c510-010000010000_2240"(
      zoo:ri!zoo,
      nameLabel: "Name",
      nameAsLink: false()
      ),
      
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Animals",
      
      firstColumnContents: {
        
        if(local!datasubset.totalCount = 0,{},
        #"SYSTEM_SYSRULES_gridField"(
        totalCount: local!datasubset.totalCount,
        columns: {
  
          #"SYSTEM_SYSRULES_gridTextColumn"(
            label: "Name",
            field: "name",
            data: index(local!datasubset.data, "name", null),
            links: apply(#"SYSTEM_SYSRULES_recordLink"(
                label: "Go to animal",
                recordType: #"_a-0000db88-8ba1-8000-c510-010000010000_2335",
                identifier: _,
                dashboard: "summary"
              ),
              index(local!datasubset.data, "id", null)
              )
          ),
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Species (Scientific)",
            field: "speciesId",
          data: index(local!datasubset.data, "speciesId", null)
        ),
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Species (Common)",
            field: "speciesId",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2480", index(local!datasubset.data, "speciesId", ""))
        ),
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Class",
            field: "class",
          data: index(#"_a-0000db88-8ba1-8000-c510-010000010000_2288", index(local!datasubset.data, "class", null), null)
        ),
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Characteristics",
            field: "characteristics",
          data: index(local!datasubset.data, "characteristics", null)
        ),
        #"SYSTEM_SYSRULES_gridImageColumn"(
          label: "Transferable",
          field: "transferable",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2613", {index(local!datasubset.data, "transferable", null)}),
