rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "status",
      ascending: true
    )
  ),
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000db88-8ba1-8000-c510-010000010000_2433",
      query: #"SYSTEM_SYSRULES_query"(
        selection: #"SYSTEM_SYSRULES_querySelection"(columns: {
          #"SYSTEM_SYSRULES_queryColumn"(field: "id"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "animalId"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "status"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "originZooId"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "destinationZooId"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "approved"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "failed"),
        }),
        logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          filters:{
            /*Filter by animal id, if provided*/
            if(isnull(ri!animalId),null,
              #"SYSTEM_SYSRULES_queryFilter"(field:"animalId", operator:"=", value: ri!animalId)
            ),
            /*Don't include canceled transfers*/
            #"SYSTEM_SYSRULES_queryFilter"(field:"status",operator:">=",value:2)
          }

        )
        ,
        pagingInfo: local!pagingInfo
      )
    ),

    /*Animals local variable is not defined if data is empty*/
  local!animals:
  if(local!datasubset.totalCount = 0,null,
    apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2255",{index(local!datasubset.data, "animalId", null)})

    ),

    /*Hide whole grid if data is empty*/
    if(local!datasubset.totalCount = 0,{},
    #"SYSTEM_SYSRULES_gridField"(
      totalCount: local!datasubset.totalCount,
      columns: {



        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Transfer",
          field: "status",
          data: #"_a-0000db88-8ba1-8000-c510-010000010000_2375"[index(local!datasubset.data, "status", null)],
          links: apply(
                #"SYSTEM_SYSRULES_recordLink"(
                  label: "Go to transfer record",
                  recordType: #"_a-0000db88-8ba1-8000-c510-010000010000_2649",
                  identifier: _,
                  dashboard: "summary"
                ),index(local!datasubset.data, "id",null)
              )
        ),
        #"SYSTEM_SYSRULES_gridImageColumn"(
          label: "Status",
          field: "status",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2643", index(local!datasubset.data, "status", null)),
          size: "ICON"
        )
        ,
        /*Hide these columns if showing for specific animal*/
        if(isnull(ri!animalId),
        {
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Animal",
          field: "animalId",
          data: apply(index(_, "name"), local!animals),
          links: apply(
                #"SYSTEM_SYSRULES_recordLink"(
                  label: "Go to animal record",
                  recordType: #"_a-0000db88-8ba1-8000-c510-010000010000_2335",
                  identifier: _,
                  dashboard: "summary"
                ),index(local!datasubset.data, "animalId",null)
              )
        )
        ,
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Species",
            field: "animalId",
          data: apply(index(_, "speciesId"), local!animals)
        )
        }
        ,{})
        ,
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Origin",
          field: "originZooId",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2631",index(local!datasubset.data, "originZooId", null)),
          links: apply(
                #"SYSTEM_SYSRULES_recordLink"(
                  label: "Go to zoo record",
                  recordType: #"_a-0000db88-8ba1-8000-c510-010000010000_2321",
                  identifier: _,
                  dashboard: "summary"
                ),index(local!datasubset.data, "originZooId",null)
              )
        ),
        #"SYSTEM_SYSRULES_gridTextColumn"(
          label: "Destination",
          field: "destinationZooId",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2631",index(local!datasubset.data, "destinationZooId", null)),
          links: apply(
                #"SYSTEM_SYSRULES_recordLink"(
                  label: "Go to zoo record",
                  recordType: #"_a-0000db88-8ba1-8000-c510-010000010000_2321",
                  identifier: _,
                  dashboard: "summary"
                ),index(local!datasubset.data, "destinationZooId",null)
              )
        ),
        #"SYSTEM_SYSRULES_gridImageColumn"(
          label: "Approval",
          field: "approved",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2622", {merge({index(local!datasubset.data, "approved", null)},{index(local!datasubset.data, "status", null)})}),
          size: "ICON"
        )
        ,
        #"SYSTEM_SYSRULES_gridImageColumn"(
          label: "Success",
          field: "failed",
          data: apply(#"_a-0000db88-8ba1-8000-c510-010000010000_2618", {index(local!datasubset.data, "failed", null)}),
          size: "ICON"
        )

      },
      value: local!pagingInfo,
      saveInto: local!pagingInfo
    )


    )
  )
)
