rule.definition:
load(
  local!filterIndex,
  local!sectionLabel: "Items",
  local!selectedItems: tointeger({}),
  with(
    /*local!gridItems: if(isnull(local!filterIndex), ri!items, index(ri!items, local!filterIndex, {})),*/
    local!a,
    #"SYSTEM_SYSRULES_formLayout"(
      label: if(ri!step="REVIEW", "Review", "Approve") & " Purchase Request",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_sectionLayout"(
          label: "Overview",
          firstColumnContents: {
            #"SYSTEM_SYSRULES_textField"(
              label: "Summary",
              readOnly: true,
              value: ri!pr.summary, 
              saveInto: ri!pr.summary
            ),
            #"SYSTEM_SYSRULES_paragraphField"(
              label: "Details",
              readOnly: true,
              value: ri!pr.details, 
              saveInto: ri!pr.details
            ),
            #"SYSTEM_SYSRULES_pickerFieldUsers"(
              label: "Requestor",
              readOnly: true,
              value: ri!pr.requestor
            ),
            #"SYSTEM_SYSRULES_dateField"(
              label: "Submitted",
              readOnly: true,
              value: ri!pr.submittedOn
            )
          },
          secondColumnContents: {
            #"009cf46e-eb44-404e-9954-de7df7a5a03e"(ri!items, local!filterIndex, local!sectionLabel),
            #"SYSTEM_SYSRULES_ButtonLayout"(
              primaryButtons: {
                #"SYSTEM_SYSRULES_ButtonWidget"(
                  label: "Clear Filters",
                  value: null,
                  saveInto: {
                    local!filterIndex << #"SYSTEM_SYSRULES_returnFirst"(1+enumerate(#"SYSTEM_SYSRULES_util_count"(ri!items)), _),
                    local!sectionLabel << #"SYSTEM_SYSRULES_returnFirst"("Items", _)
                  }
                )
              }
            )
          }
        ),
        #"SYSTEM_SYSRULES_sectionLayout"(
          label: local!sectionLabel,
          firstColumnContents: {
            #"76cb83ce-251e-4cda-b65a-1afe3f2bba06"(
              items: ri!items,
              step: ri!step,
              filteredIndices: if(#"SYSTEM_SYSRULES_util_count"(local!filterIndex)=0, 
                1+enumerate(#"SYSTEM_SYSRULES_util_count"(ri!items)), 
                local!filterIndex
              ),
              selectedItems: local!selectedItems
            ) 
          }
        )
      },
      buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
        primaryButtons: #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
          label: "Submit",
          style: "PRIMARY",
          value: loggedInUser(),
          saveInto: if(ri!step="APPROVAL", ri!pr.approvedBy, null)
        )
      )
    )
  )
)
